package loops4;

public class SingleDimArrayWithLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name[]=new String[3];
		
		name[0]="Nisha";
	    name[1]="Khusbu";
	    name[2]="Shweta";

	    for (int i=0;i<=2;i++) {
	    	
	    	System.out.println(name[i]);
	    }
	}

}
